import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  let navigate = useNavigate();

  // validate the login
  let login_validator = () => {
    if (localStorage.getItem("username") !== "foo") {
      navigate("/");
    } else if (localStorage.getItem("password") !== "bar") {
      navigate("/");
    }
  };

  // logout functionality
  let logout_handler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    login_validator();
  }, []);

  //api calls
  const [data, setData] = useState("");
  const userAPI = "https://randomuser.me/api/?results=15";

  let userList = async () => {
    try {
      let response = await axios
        .get(userAPI)
        .then((e) => setData(e.data.results));
      console.log(data);
    } catch (err) {
      console.error(err, "error");
    }
  };
  useEffect(() => {
    userList();
  }, []);
  return (
    <div style={{ margin: "20px" }}>
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button variant="outlined" onClick={logout_handler}>
          Logout
        </Button>
      </div>

      <div>
        {data ? (
          data.map((e) => {
            return (
              <div className="row">
                <div className="col-lg-4 col-md-3">
                  <Card key={e.id.name} sx={{ width: 320, height: 480 }}>
                    <CardMedia
                      sx={{ width: 320, height: 480 }}
                      image={e.picture.large}
                      title={e.name.title}
                    />
                    <CardContent>
                      <Typography variant="h5">
                        {e.name.title + " " + e.name.first + " " + e.name.last}
                      </Typography>
                    </CardContent>
                  </Card>
                </div>
              </div>
            );
          })
        ) : (
          <Skeleton variant="rectangular" width={320} height={480} />
        )}
      </div>
    </div>
  );
}

export default Home;
