import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";

function Home() {
  const navigate = useNavigate();

  // Validate the login
  const loginValidator = () => {
    if (localStorage.getItem("username") !== "foo" || localStorage.getItem("password") !== "bar") {
      navigate("/");
    }
  };

  // Logout functionality
  const logoutHandler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    loginValidator();
  }, []);

  // Calculate height of the page
  const [numberOfCards, setNumberOfCards] = useState(0);

  useEffect(() => {
    const calculateNumberOfCards = () => {
      const element = document.documentElement;
      const height = element.clientHeight;
      const calculatedNumberOfCards = Math.floor(height / 345);
      setNumberOfCards(calculatedNumberOfCards);
    };

    calculateNumberOfCards();

    window.addEventListener("resize", calculateNumberOfCards);
    return () => {
      window.removeEventListener("resize", calculateNumberOfCards);
    };
  }, []);

  // API calls
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const userAPI = "https://randomuser.me/api/";

  const fetchUserList = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(userAPI);
      setData((prevData) => [...prevData, ...response.data.results]);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUserList();
  }, []);

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        backgroundSize: "cover",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={logoutHandler}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Logout
        </Button>
      </div>

      <div style={{ margin: "20px", height: '88vh' }}>
        {data.length ? (
          data.slice(0, numberOfCards).map((user) => (
            <div key={user.id.name} style={{ height: '88vh' }}>
              <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
                <CardMedia
                  sx={{ height: 100 }}
                  image={user.picture.large}
                  title={`${user.name.title} ${user.name.first}`}
                />
                <CardContent>
                  <Typography>
                    {`${user.name.title} ${user.name.first} ${user.name.last}`}
                  </Typography>
                </CardContent>
              </Card>
            </div>
          ))
        ) : (
          <Skeleton variant="rectangular" width={320} height={480} />
        )}
      </div>
    </div>
  );
}

export default Home;
