import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  let navigate = useNavigate();

  // validate the login
  let login_validator = () => {
    if (localStorage.getItem("username") !== "foo") {
      navigate("/");
    } else if (localStorage.getItem("password") !== "bar") {
      navigate("/");
    }
  };

  // logout functionality
  let logout_handler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    login_validator();
  }, []);

  //api calls
  const [data, setData] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const numberOfUsers = 15;
  const userAPI = "https://randomuser.me/api/?results=" +  numberOfUsers ;

  let userList = async () => {
    setIsLoading(true);
    try {
      let response = await axios.get(userAPI);
      setData((prevData) => [...prevData, ...response.data.results]);
      setIsLoading(false);
      console.log(data);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
  };
  useEffect(() => {
    userList();
  }, []);
  return (
    <div style={{ margin: "20px" }}>
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button variant="outlined" onClick={logout_handler}>
          Logout
        </Button>
      </div>

      <div className="row">
        {data ? (
          data.map((e) => {
            return (
              <div className="col-lg-3 col-md-4">
                <Card key={e.id.name} sx={{ maxWidth: 345, marginBottom: 20 }}>
                  <CardMedia
                    sx={{ height: 480 }}
                    image={e.picture.large}
                    title={e.name.title + " " + e.name.first}
                  />
                  <CardContent>
                    <Typography variant="h5">
                      {e.name.title + " " + e.name.first + " " + e.name.last}
                    </Typography>
                  </CardContent>
                </Card>
              </div>
            );
          })
        ) : (
          <Skeleton variant="rectangular" width={320} height={480} />
        )}
      </div>
    </div>
  );
}

export default Home;
