import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";

function Home() {
  const navigate = useNavigate();
  const PAGE_SIZE = 4;
  const LOAD_DELAY = 1000;

  const loginValidator = () => {
    if (
      localStorage.getItem("username") !== "foo" ||
      localStorage.getItem("password") !== "bar"
    ) {
      navigate("/");
    }
  };

  const logoutHandler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    loginValidator();
  }, []);

  const [numberOfCard, setNumberOfCard] = useState(0);

  useEffect(() => {
    const updateNumberOfCard = () => {
      const height = document.documentElement.clientHeight;
      const calculatedNumberOfCard = Math.floor(height / 100);
      setNumberOfCard(calculatedNumberOfCard);
      console.log(numberOfCard);
    };

    updateNumberOfCard();
    window.addEventListener("resize", updateNumberOfCard);

    return () => {
      window.removeEventListener("resize", updateNumberOfCard);
    };
  }, []);

  const [userList, setUserList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(1);

  const userAPI = `https://randomuser.me/api/?results=${PAGE_SIZE}&page=${page}`;

  const fetchUserList = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(userAPI);
      setUserList((prevUserList) => [
        ...prevUserList,
        ...response.data.results.slice(0, PAGE_SIZE),
      ]);

      setTimeout(() => {
        setIsLoading(false);
      }, LOAD_DELAY);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUserList();
  }, [page]);

  const handleScroll = () => {
    if (
      window.innerHeight + window.scrollY >=
      document.documentElement.offsetHeight
    ) {
      setPage((prevPage) => prevPage + 1);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        backgroundSize: "cover",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={logoutHandler}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Logout
        </Button>
      </div>

      <div style={{ margin: "20px", height: "88vh" }}>
        {userList.length === 0 ? (
          <Skeleton variant="rectangular" width={220} height={100} />
        ) : (
          userList.map((user) => (
            <div key={user.id.name}>
              <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
                <CardMedia
                  sx={{ height: 100 }}
                  image={user.picture.large}
                  title={`${user.name.title} ${user.name.first}`}
                />
                <CardContent>
                  <Typography>
                    {`${user.name.title} ${user.name.first} ${user.name.last}`}
                  </Typography>
                </CardContent>
              </Card>
            </div>
          ))
        )}
        {isLoading && <div>Loading...</div>}
      </div>
    </div>
  );
}

export default Home;
