import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";

function Home() {
  const [numberOfCard, setNumberOfCard] = useState(10);
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const fetchUserList = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(`https://randomuser.me/api/?results=${numberOfCard}`);
      setData(response.data.results);
      setIsLoading(false);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUserList();
  }, [numberOfCard]);

  const handleScroll = () => {
    if (document.body.scrollHeight - window.innerHeight <= window.scrollY) {
      setNumberOfCard(numberOfCard + 10);
      fetchUserList();
    }
  };

  window.addEventListener("scroll", handleScroll);

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        backgroundSize: "cover",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={() => setNumberOfCard(numberOfCard + 10)}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Load more
        </Button>
      </div>

      <div style={{ margin: "20px", height: "88vh" }}>
        {data.map((user) => (
          <div key={user.id.name}>
            {isLoading ? (
              <Skeleton variant="rectangular" width={220} height={100} />
            ) : (
              <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
                <CardMedia
                  sx={{ height: 100 }}
                  image={user.picture.large}
                  title={`${user.name.title} ${user.name.first}`}
                />
                <CardContent>
                  <Typography>
                    {`${user.name.title} ${user.name.first} ${user.name.last}`}
                  </Typography>
                </CardContent>
              </Card>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
