import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";

function Home() {
  const navigate = useNavigate();
  const PAGE_SIZE = 4;

  const loginValidator = () => {
    if (
      localStorage.getItem("username") !== "foo" ||
      localStorage.getItem("password") !== "bar"
    ) {
      navigate("/");
    }
  };

  const logoutHandler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    loginValidator();
  }, []);

  const [numberOfCard, setNumberOfCard] = useState(5);

  useEffect(() => {
    const updateNumberOfCard = () => {
      const height = document.documentElement.clientHeight;
      const calculatedNumberOfCard = Math.floor(height / 100);
      setNumberOfCard(calculatedNumberOfCard);
    };

    updateNumberOfCard();
    window.addEventListener("resize", updateNumberOfCard);

    return () => {
      window.removeEventListener("resize", updateNumberOfCard);
    };
  }, []);

  const [userList, setUserList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const sentinelRef = useRef(null);

  const userAPI = `https://randomuser.me/api/?results=${PAGE_SIZE}`;

  const fetchUserList = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(userAPI);
      const newData = response.data.results;
      setUserList((prevUserList) => [...prevUserList, ...newData]);
      setIsLoading(false);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUserList();
  }, []);

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: "0px",
      threshold: 1.0,
    };

    const handleIntersection = (entries) => {
      const entry = entries[0];
      if (entry.isIntersecting) {
        fetchUserList();
      }
    };

    const observer = new IntersectionObserver(handleIntersection, options);
    if (sentinelRef.current) {
      observer.observe(sentinelRef.current);
    }

    return () => {
      if (sentinelRef.current) {
        observer.unobserve(sentinelRef.current);
      }
    };
  }, [userList]);

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        backgroundSize: "cover",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={logoutHandler}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Logout
        </Button>
      </div>

      <div style={{ margin: "20px", height: "88vh" }}>
        {userList.map((user, index) => (
          <div key={index}>
            <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
              <CardMedia
                sx={{ height: 100 }}
                image={user.picture.large}
                title={`${user.name.title} ${user.name.first}`}
              />
              <CardContent>
                <Typography>
                  {`${user.name.title} ${user.name.first} ${user.name.last}`}
                </Typography>
              </CardContent>
            </Card>
            {index === userList.length - 1 && <div ref={sentinelRef} />}
          </div>
        ))}
        {isLoading && <div>Loading...</div>}
      </div>
    </div>
  );
}

export default Home;
