import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  let navigate = useNavigate();

  // validate the login
  let login_validator = () => {
    if (localStorage.getItem("username") !== "foo") {
      navigate("/");
    } else if (localStorage.getItem("password") !== "bar") {
      navigate("/");
    }
  };

  // logout functionality
  let logout_handler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    login_validator();
  }, []);

  //api calls
  const [data, setData] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const userAPI = "https://randomuser.me/api/";

  let userList = async () => {
    setIsLoading(true);
    try {
      let response = await axios.get(userAPI);
      setData((prevData) => [...prevData, ...response.data.results]);
      setIsLoading(false);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
    console.log(data);
  };
  useEffect(() => {
    userList();
  }, []);

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={logout_handler}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Logout
        </Button>
      </div>

      <div className="" style={{ margin: "20px" }}>
        {data ? (
          data.map((e) => {
            return (
              <div className="">
                <Card key={e.id.name} sx={{ maxWidth: 220, marginBottom: 2 }}>
                  <CardMedia
                    sx={{ height: 100 }}
                    image={e.picture.large}
                    title={e.name.title + " " + e.name.first}
                  />
                  <CardContent>
                    <Typography>
                      {e.name.title + " " + e.name.first + " " + e.name.last}
                    </Typography>
                  </CardContent>
                </Card>
              </div>
            );
          })
        ) : (
          <Skeleton variant="rectangular" width={320} height={480} />
        )}
      </div>
    </div>
  );
}

export default Home;
