import {
    Button,
    Card,
    CardContent,
    CardMedia,
    Skeleton,
    Typography,
  } from "@mui/material";
  import axios from "axios";
  import React, { useEffect, useState } from "react";
  import { useNavigate } from "react-router-dom";
  
  function Home() {
    let navigate = useNavigate();
  
    // validate the login
    let login_validator = () => {
      if (localStorage.getItem("username") !== "foo") {
        navigate("/");
      } else if (localStorage.getItem("password") !== "bar") {
        navigate("/");
      }
    };
  
    // logout functionality
    let logout_handler = () => {
      localStorage.setItem("username", "");
      localStorage.setItem("password", "");
      navigate("/");
    };
  
    useEffect(() => {
      login_validator();
    }, []);
  
    //api calls
    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const userAPI = "https://randomuser.me/api/";
  
    let userList = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(userAPI);
        setData((prevData) => [...prevData, ...response.data.results]);
        setIsLoading(false);
      } catch (err) {
        console.error(err, "error");
        setIsLoading(false);
      }
    };
  
    useEffect(() => {
      userList();
    }, []);
  
    useEffect(() => {
      const handleScroll = () => {
        if (
          window.innerHeight + document.documentElement.scrollTop ===
          document.documentElement.offsetHeight
        ) {
          setIsLoading(true);
          setTimeout(() => {
            userList();
          }, 1000);
        }
      };
  
      window.addEventListener("scroll", handleScroll);
  
      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }, []);
  
    return (
      <div
        style={{
          margin: "20px",
          backgroundImage:
            "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        }}
      >
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <Button
            variant="outlined"
            onClick={logout_handler}
            sx={{ backgroundColor: "gold", color: "#000" }}
          >
            Logout
          </Button>
        </div>
  
        <div className="row" style={{ margin: "20px" }}>
          {data.length > 0 ? (
            data.map((e, index) => (
              <div className="col-lg-3 col-md-4" key={index}>
                <Card sx={{ maxWidth: 345, marginBottom: 20 }}>
                  <CardMedia
                    sx={{ height: 180 }}
                    image={e.picture.large}
                    title={e.name.title + " " + e.name.first}
                  />
                  <CardContent>
                    <Typography variant="h5">
                      {e.name.title + " " + e.name.first + " " + e.name.last}
                    </Typography>
                  </CardContent>
                </Card>
              </div>
            ))
          ) : (
            <Skeleton variant="rectangular" width={320} height={480} />
          )}
  
          {isLoading && (
            <div style={{ textAlign: "center", margin: "20px" }}>
              <Typography>Loading...</Typography>
            </div>
          )}
        </div>
      </div>
    );
  }
  
  export default Home;
