import React, { useState, useEffect } from "react";
import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";

const Home = () => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [lastItem, setLastItem] = useState(null);

  const handleScroll = () => {
    if (
      document.documentElement.clientHeight - window.scrollY <= 100 &&
      !isLoading &&
      lastItem !== null
    ) {
      setIsLoading(true);
      setTimeout(() => {
        const requestURL = `https://randomuser.me/api/?results=10&seed=${lastItem.id}`;
        const fetchData = async () => {
          const response = await fetch(requestURL);
          const data = await response.json();
          return data.results;
        };

        const newData = [...data, lastItem];
        setData(newData);
        setIsLoading(false);
      }, 1000);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div style={{ margin: "20px" }}>
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={() => setData([])}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Reset
        </Button>
      </div>

      <div style={{ margin: "20px" }}>
        {data.map((user) => (
          <div key={user.id}>
            <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
              <CardMedia
                sx={{ height: 100 }}
                image={user.picture.large}
                title={`${user.name.title} ${user.name.first}`}
              />
              <CardContent>
                <Typography>
                  {`${user.name.title} ${user.name.first} ${user.name.last}`}
                </Typography>
              </CardContent>
            </Card>
          </div>
        ))}
        {isLoading && <Skeleton variant="rectangular" width={220} height={100} />}
      </div>
    </div>
  );
};

export default Home;
