import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Skeleton,
  Typography,
} from "@mui/material";

function Home() {
  const navigate = useNavigate();

  // Validate the login
  const loginValidator = () => {
    if (
      localStorage.getItem("username") !== "foo" ||
      localStorage.getItem("password") !== "bar"
    ) {
      navigate("/");
    }
  };

  // Logout functionality
  const logoutHandler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    loginValidator();
  }, []);

  // API calls
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isEndReached, setIsEndReached] = useState(false);

  const userAPI = "https://randomuser.me/api/";
  const partialListSize = 10; // Number of users to load initially
  const loadDelay = 1000; // Delay in milliseconds to simulate loading

  const fetchUserList = async (results = 10) => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${userAPI}?results=${results}`);
      setData((prevData) => [...prevData, ...response.data.results]);
      setIsLoading(false);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUserList(partialListSize);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + document.documentElement.scrollTop ===
        document.documentElement.offsetHeight
      ) {
        if (!isLoading && !isEndReached) {
          setIsEndReached(true);
          setIsLoading(true);
          setTimeout(() => {
            fetchUserList();
            setIsLoading(false);
            setIsEndReached(false);
          }, loadDelay);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [isLoading, isEndReached]);

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        backgroundSize: "cover",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={logoutHandler}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Logout
        </Button>
      </div>

      <div style={{ margin: "20px", height: "88vh" }}>
        {data.map((user) => (
          <div key={user.id.name}>
            <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
              <CardMedia
                sx={{ height: 100 }}
                image={user.picture.large}
                title={`${user.name.title} ${user.name.first}`}
              />
              <CardContent>
                <Typography>
                  {`${user.name.title} ${user.name.first} ${user.name.last}`}
                </Typography>
              </CardContent>
            </Card>
          </div>
        ))}
        {isLoading && (
          <div style={{ display: "flex", justifyContent: "center" }}>
            <Skeleton variant="rectangular" width={320} height={480} />
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
