import { Button } from "@mui/material";
import axios from "axios";
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  let navigate = useNavigate();

  // validate the login
  let login_validator = () => {
    if (localStorage.getItem("username") !== "foo") {
      navigate("/");
    } else if (localStorage.getItem("password") !== "bar") {
      navigate("/");
    }
  };

  // logout functionality
  let logout_handler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    login_validator();
  }, []);

  //api calls

  const userAPI = "https://randomuser.me/api/";

  let userList = async () => {
    try {
      let response = await axios.get("userAPI").then((e) => console.log(e));
    } catch (err) {
      console.error(err, "error");
    }
  };
  useEffect(() => {
    userList();
  }, []);
  return (
    <div>
      <h3>This is home</h3>
      <Button variant="outlined" onClick={logout_handler}>
        Logout
      </Button>
    </div>
  );
}

export default Home;
