import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Typography,
} from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  let navigate = useNavigate();

  // validate the login
  let login_validator = () => {
    if (localStorage.getItem("username") !== "foo") {
      navigate("/");
    } else if (localStorage.getItem("password") !== "bar") {
      navigate("/");
    }
  };

  // logout functionality
  let logout_handler = () => {
    localStorage.setItem("username", "");
    localStorage.setItem("password", "");
    navigate("/");
  };

  useEffect(() => {
    login_validator();
  }, []);

  //api calls
  const [data, setData] = useState("");
  const userAPI = "https://randomuser.me/api/?results=15";

  let userList = async () => {
    try {
      let response = await axios
        .get(userAPI)
        .then((e) => setData(e.data.results));
      console.log(data);
    } catch (err) {
      console.error(err, "error");
    }
  };
  useEffect(() => {
    userList();
  }, []);
  return (
    <div>
     
      <Button variant="outlined" onClick={logout_handler}>
        Logout
      </Button>
      <div>
        {data
          ? data.map((e) => {
              return (
                <Card key={e.id.name}>
                  <CardMedia
                    sx={{ height: 140, width: 200 }}
                    image={e.picture.medium}
                    title={e.name.title}
                  />
                  <CardContent>
                    <Typography>
                    {e.name.title + ' ' + e.name.first + ' ' + e.name.last}
                    </Typography>
                  </CardContent>
                </Card>
              );
            })
          : ""}
      </div>
    </div>
  );
}

export default Home;
