import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  CardMedia,
  CardContent,
  Skeleton,
  Typography,
} from "@mui/material";
import { useNavigate } from "react-router-dom";

const PAGE_SIZE = 4;
const LOAD_DELAY = 1000;

const Home = () => {
  const [userList, setUserList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(1);

  const userAPI = `https://randomuser.me/api/?results=${PAGE_SIZE}&page=${page}`;

  const fetchUserList = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get(userAPI);
      setUserList((prevUserList) => [
        ...prevUserList,
        ...response.data.results.slice(0, PAGE_SIZE),
      ]);

      setTimeout(() => {
        setIsLoading(false);
      }, LOAD_DELAY);
    } catch (err) {
      console.error(err, "error");
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUserList();
  }, [page]);

  const handleScroll = () => {
    if (
      window.innerHeight + window.scrollY >=
      document.documentElement.offsetHeight
    ) {
      setPage((prevPage) => prevPage + 1);
      fetchUserList();
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const renderUserList = () => {
    return userList.map((user, index) => (
      <div key={user.id.name}>
        <Card sx={{ maxWidth: 220, marginBottom: 2 }}>
          <CardMedia
            sx={{ height: 100 }}
            image={user.picture.large}
            title={`${user.name.title} ${user.name.first}`}
          />
          <CardContent>
            <Typography>
              {`${user.name.title} ${user.name.first} ${user.name.last}`}
            </Typography>
          </CardContent>
        </Card>
        {isLoading && index === userList.length - 1 && (
          <Skeleton variant="rectangular" width={220} height={100} />
        )}
      </div>
    ));
  };

  const logoutHandler = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("password");
    useNavigate("/");
  };

  return (
    <div
      style={{
        margin: "20px",
        backgroundImage:
          "url('https://cdn.pixabay.com/photo/2016/11/29/05/45/astronomy-1867616_640.jpg')",
        backgroundSize: "cover",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end" }}>
        <Button
          variant="outlined"
          onClick={logoutHandler}
          sx={{ backgroundColor: "gold", color: "#000" }}
        >
          Logout
        </Button>
      </div>

      <div style={{ margin: "20px", height: "88vh" }}>
        {renderUserList()}
      </div>
    </div>
  );
};

export default Home;
